import React, { useState } from 'react'
import { Link, useLocation } from 'react-router-dom'

const Navbar = () => {
  let location = useLocation()
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleClick = () => {
    if (!isLoggedIn) {
      // Redirect to login page
      window.location.href = 'login';
    } else {
      // Logout and change button text add logout logic, ie. checking for local storage
      setIsLoggedIn(false);
    }
  }

  return (
    <nav className="bg-light sticky-top px-0 py-0">
      <div className="flex items-center justify-between">
        <Link className="text-2xl font-bold" to="/fas">
          <img className="w-10 h-10" src="../tu_logo.svg" alt="icon" />
        </Link>
        <div className="space-x-4">
          <Link className={`text-lg ${location.pathname === "/fas/home" ? "font-bold" : ""}`} to="/fas">Home</Link>
          <Link className={`text-lg ${location.pathname === "/fas/aboutus" ? "font-bold" : ""}`} to="/fas/aboutus">About Us</Link>
          <Link className={`text-lg ${location.pathname === "/fas/contactus" ? "font-bold" : ""}`} to="/fas/contactus">Contact Us</Link>
          <button className="bg-blue-200 text-lg px-4 py-2 rounded" onClick={handleClick}>
            {isLoggedIn ? 'Logout' : 'Login'} <i className="fa fa-sign-in" aria-hidden="true"></i>
          </button>
        </div>
      </div>
    </nav>
  )
}

export default Navbar;
