import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [error, setError] = useState(null);
  const [redirectToDashboard, setRedirectToDashboard] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (email) {
      // Call login API
      fetch('http://localhost:8000/teachers/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email }),
      })
      .then(response => response.json())
      .then(data => {
        if (data.error) {
          setError(data.error);
        } else {
          setError(null);
          alert('Email found, OTP sent.');
          // You can replace this with code to handle successful login
          setRedirectToDashboard(true);
        }
      })
      .catch((error) => {
        console.error('Error:', error);
        setError('An error occurred while logging in.');
      });
    } else {
      setError('Please enter your email');
    }
  }

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-blue-200 p-6">
      <form className="w-full max-w-md bg-white rounded shadow-md p-6" onSubmit={handleSubmit}>
        <h3 className="text-2xl font-bold mb-4 text-center">Login</h3>
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2">Email address</label>
          <input type="email" className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Enter email" />
          <p className="text-gray-600 text-xs italic">Please enter your pcampus email.</p>
        </div>
        <div className="flex items-center justify-center">
          <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" type="submit">Submit</button>
        </div>
        {error && <p className="text-red-500 text-xs italic mt-4">{error}</p>}
        {redirectToDashboard && <Navigate to="/fas/dashboard" replace />}
      </form>
    </div>
  )
}

export default Login;
